SET BOARD_TITLE = #{board_title}, BOARD_CONTENT = #{board_content}
WHERE BOARD_NO = #{board_no}

UPDATE BOARD
SET BOARD_STATUS = 'N'
WHERE BOARD_NO = #{board_no}

